// Tenta buscar o valor atual do contador da 'memória' do fluxo.
// Se não existir (primeira vez), define como 0.
let contador = flow.get('contadorPecas') || 0;

// VERIFICA SE É UM COMANDO DE RESET
if (msg.topic === 'reset') {
    contador = 0;
    flow.set('contadorPecas', contador); // Salva o valor zerado na memória
    node.status({fill:"red", shape:"dot", text:"Contador resetado para 0"});
    return null; // Para a execução, não envia nada para a API
}

// Se não for reset, incrementa o contador.
contador++;

// Salva o novo valor do contador na 'memória' do fluxo para a próxima vez.
flow.set('contadorPecas', contador);

// Exibe o valor atual no status do nó para fácil visualização.
node.status({fill:"green", shape:"dot", text:"Peças: " + contador});

